mapper.update:
UPDATE notice_board
SET hit = hit + 1
WHERE no = #{no}

mapper.select:
	SELECT no, name, type, subject, content, hit, TO_CHAR(regdate, 'YYYY-MM-DD HH24:MI:SS') as dbday
 	FROM notice_board 
 	WHERE no = #{no}

mapper.update:
UPDATE notice_board 
SET type = #{type}, subject = #{subject}, content = #{content}
WHERE no = #{no}
